import './App.css';
import Header from "./MyComponents/Header";
import Todos from "./MyComponents/Todos";
import Footer from "./MyComponents/Footer";
import AddTodo from "./MyComponents/AddTodo";
import React, { useState } from 'react';


function App() {
  const onDelete = (todo) => {
    console.log("I am onDelete of todo", todo);

    // Deleting this way in React does not Works!!
    // let index = todos.indexof(todo);
    // todos.splice(index,1);

    setTodos(todos.filter((e) => {
      return e!== todo;

    }))
  }
  const addTodo=(title,desc)=>{
    console.log("I am adding this todo",title,desc)
  }
  const [todos, setTodos] = useState([
    {
      sno: 1,
      title: "Go to office",
      desc: "You need to go to the office to this job done..!"

    },
    {
      sno: 2,
      title: "Go to market",
      desc: "You need to go to the market to bring the groceries..!"

    },
    {
      sno: 3,
      title: "Go to mall",
      desc: "You need to go to the mall to buy clean new clothes..!"

    }
  ])
  return (
    <>
      <Header title="MyTodos List" searchBar={true} />
      <AddTodo addTodo={AddTodo}/>
      <Todos todos={todos} onDelete={onDelete} />
      <Footer />
    </>
  );
}

export default App;
